mapper.select:
SELECT A.*, B.* FROM ( SELECT TT1.num, TT1.category, TT1.name, TT1.url FROM ( SELECT @rownum := @rownum + 1 AS num, T1.* FROM ( SELECT * FROM UPLOAD_IMAGE WHERE category = #{category} AND checked = 0 ORDER BY upload_date DESC ) T1, (SELECT @rownum := 0) AS r ) TT1
WHERE TT1.num BETWEEN ((#{pageNo} - 1) * #{pageSize}) + 1 AND #{pageNo} * #{pageSize}
) A CROSS JOIN ( SELECT COUNT(*) AS totalCnt FROM UPLOAD_IMAGE WHERE category = #{category} AND checked = 0 ) B

SELECT A.*, B.* FROM ( SELECT TT1.num, TT1.category, TT1.name, TT1.url FROM ( SELECT @rownum := @rownum + 1 AS num, T1.* FROM ( SELECT * FROM UPLOAD_IMAGE WHERE category in (10, 20, 30) AND checked = 0 ORDER BY upload_date DESC ) T1, (SELECT @rownum := 0) AS r ) TT1
WHERE TT1.num BETWEEN ((#{pageNo} - 1) * #{pageSize}) + 1 AND #{pageNo} * #{pageSize}
) A CROSS JOIN ( SELECT COUNT(*) AS totalCnt FROM UPLOAD_IMAGE WHERE category in (10, 20, 30) AND checked = 0 ) B
